          <p id="unique">We have fruits and vegetables</p>
          <div className="container">
          <h3>All Products</h3>
          <div className="products">
            {product.map(item =>(
          <div key={item.id}>
              <p> {item.name}</p> 
          </div>
        ))} </div>

          </div>
        </div>
      )
  }
  export default Products;
